import React from "react";

import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";

import NavBar from "./components/NavBar";
import Home from "./components/Home";
import { BrowserRouter as Router, Route } from "react-router-dom";
import Reservations from "./components/Reservations";
import DishDetails from "./components/DishDetails";
import Menu from "./components/Menu";

class App extends React.Component {
  state = { 
    show
  };

  showSearchResult = (searchString) => {
    this.setState({ searchedLoading: true });

    fetch(`http://www.omdbapi.com/?apikey=85a2b045&s=${searchString}`)
      .then((response) => response.json())
      .then((responseObject) => {
        console.log(responseObject);
        if (responseObject.Response === "True") {
          this.setState({
            searchedMovies: responseObject.Search,
          });
        }
        this.setState({ searchedLoading: false });
      })
      .catch((err) => {
        this.setState({ searchedLoading: null });
      });
  };
  render() {
    return (
      <>
        <Router>
          <NavBar title="Strivestaurant"/>
          <Route
            path="/"
            exact
            render={(
              props 
            ) => <Home title="Stefano" {...props} />} 
          />
          <Route path="/menu" exact component={Menu} />
          <Route
            path="/reservation"
            exact
            render={(
              props
            ) => <Reservations header="AAAAAA" {...props} />} 
          />
          <Route path="/details/:stefano:title" component={DishDetails} />
        </Router>
      </>
    );
  }
}

export default App;
